import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Box, 
  Typography, 
  Paper, 
  CircularProgress,
  Alert,
  LinearProgress,
  Divider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button
} from '@mui/material';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [progress, setProgress] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [languages, setLanguages] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('eng');
  const [fileUrl, setFileUrl] = useState('');

  useEffect(() => {
    // Fetch supported languages
    axios.get('http://localhost:5000/api/languages')
      .then(response => {
        setLanguages(response.data);
      })
      .catch(err => {
        console.error('Error fetching languages:', err);
      });
  }, []);

  const onDrop = async (acceptedFiles) => {
    if (fileUrl) return; // Prevent file upload if URL is present
    const file = acceptedFiles[0];
    setFile(file);
    setLoading(true);
    setError('');
    setResult('');
    setProgress(0);
    setTotalPages(0);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('language', selectedLanguage);

    try {
      const response = await axios.post('http://localhost:5000/api/ocr', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        },
      });

      setResult(response.data.text);
      if (response.data.pages) {
        setTotalPages(response.data.pages);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred during OCR processing');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlProcess = async () => {
    if (!fileUrl) return;
    setFile(null);
    setLoading(true);
    setError('');
    setResult('');
    setProgress(0);
    setTotalPages(0);

    const formData = new FormData();
    formData.append('file_url', fileUrl);
    formData.append('language', selectedLanguage);

    try {
      const response = await axios.post('http://localhost:5000/api/ocr', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResult(response.data.text);
      if (response.data.pages) {
        setTotalPages(response.data.pages);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred during OCR processing');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    const blob = new Blob([result], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'ocr_result.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.png', '.jpg', '.jpeg', '.tiff'],
      'application/pdf': ['.pdf'],
      'application/x-djvu': ['.djvu']
    },
    multiple: false,
    disabled: !!fileUrl
  });

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Wikisource OCR
        </Typography>

        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel id="language-select-label">Language</InputLabel>
          <Select
            labelId="language-select-label"
            value={selectedLanguage}
            label="Language"
            onChange={(e) => setSelectedLanguage(e.target.value)}
          >
            {languages.map((lang) => (
              <MenuItem key={lang.code} value={lang.code}>
                {lang.name} ({lang.script})
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <Box sx={{ mb: 2 }}>
          <TextField
            label="PDF/DjVu File URL (e.g. Wikimedia Commons)"
            variant="outlined"
            fullWidth
            value={fileUrl}
            onChange={e => setFileUrl(e.target.value)}
            disabled={!!file}
            sx={{ mb: 1 }}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleUrlProcess}
            disabled={!fileUrl || loading}
            sx={{ mb: 2 }}
          >
            Process from URL
          </Button>
        </Box>

        <Paper
          {...getRootProps()}
          sx={{
            p: 3,
            textAlign: 'center',
            cursor: fileUrl ? 'not-allowed' : 'pointer',
            bgcolor: isDragActive ? 'action.hover' : 'background.paper',
            border: '2px dashed',
            borderColor: isDragActive ? 'primary.main' : 'grey.300',
            mb: 3,
            opacity: fileUrl ? 0.5 : 1
          }}
        >
          <input {...getInputProps()} disabled={!!fileUrl} />
          {loading ? (
            <Box sx={{ width: '100%' }}>
              <CircularProgress />
              <Typography variant="body2" sx={{ mt: 1 }}>
                Processing...
              </Typography>
              {totalPages > 0 && (
                <Typography variant="body2" sx={{ mt: 1 }}>
                  Processing {totalPages} pages
                </Typography>
              )}
            </Box>
          ) : (
            <Typography>
              {isDragActive
                ? "Drop the file here"
                : fileUrl
                  ? "File upload disabled when URL is provided"
                  : "Drag and drop a file here, or click to select"}
            </Typography>
          )}
        </Paper>

        {file && (
          <Typography variant="body1" gutterBottom>
            Selected file: {file.name}
          </Typography>
        )}
        {fileUrl && (
          <Typography variant="body1" gutterBottom>
            URL: {fileUrl}
          </Typography>
        )}

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {result && (
          <Paper sx={{ p: 2, whiteSpace: 'pre-wrap', mb: 2 }}>
            <Typography variant="h6" gutterBottom>
              OCR Result
            </Typography>
            <Divider sx={{ mb: 2 }} />
            <Typography variant="body1">
              {result}
            </Typography>
          </Paper>
        )}
        {result && (
          <Button variant="outlined" color="primary" onClick={handleDownload} sx={{ mb: 2 }}>
            Download TXT
          </Button>
        )}
      </Box>
    </Container>
  );
}

export default App;
